const typeDefinitions = `
  directive @auth on QUERY | FIELD_DEFINITION | FIELD

  type User {
    id: Int
    avatar: String
    username: String
  }
  type File {
    filename: String!
    url: String!
  }

  input UserInput {
    username: String!
    avatar: String!
  }

  type Response {
    success: Boolean
  }

  type UsersSearch {
    users: [User]
  }

  type Auth {
    token: String
  }

  type RootMutation {
    login (
      email: String!
      password: String!
    ): Auth
    signup (
      username: String!
      email: String!
      password: String!
    ): Auth
  }

  type RootQuery {
    usersSearch(page: Int, limit: Int, text: String!): UsersSearch 
    currentUser: User @auth
  }

  schema {
    query: RootQuery
    mutation: RootMutation
  }
`;

export default [typeDefinitions];

